mapper.select:
select
from film_info where film_name like concat(concat('%',#{filmName,jdbcType=VARCHAR}),'%') and black = '1'
limit #{start},#{size}


select   from film_info 
where black = '1'

          limit #{start},#{size}

mapper.delete:
update film_info
set 	black = '0'
where id = #{id,jdbcType=INTEGER}
